import React from 'react';

class DrumPad extends React.Component {

    render(){
        const {keyCode, keyTrigger, id, url} = this.props;

        return (
        <button
            id={id}
            className="drum-pad"
        >
            {keyTrigger}
            <audio id={keyTrigger} src={url} type="audio/mp3"  className="clip"></audio>
        </button>
        )
    }
};

export default DrumPad;
