import React from 'react';
import bankOne from './Components/audioBank/bankOne';
import bankTwo from './Components/audioBank/bankTwo';
import PadBank from './Components/PadBank';
import on from './Components/images/on.png';

class App extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            power: true,
            display: '',
            currentPadBank: bankOne,
            currentPadBankId: 'Heater Kit',
            sliderVal: 0.3
        };
    };

    displayClipName = (name) => {
        if (this.state.power) {
            this.setState({
                display: name
            });
        };
    };

    selectBank = () => {
        if (this.state.power) {
            this.state.currentPadBankId === 'Heater Kit' ? 
                this.setState({
                    currentPadBank: bankTwo,
                    currentPadBankId: 'Smooth Piano Kit',
                }) :
                this.setState({
                    currentPadBank: bankOne,
                    currentPadBankId: 'Heater Kit',
                });
        };
    };

    adjustVolume = () => {
        console.log("adjust volume")
    };

    powerControl = () => {
        this.setState({
            power: !this.state.power
        });
    };

    clearDisplay = () => {
        this.setState({
            display: ''
        });
    };

    render(){
        return(
            <div id="drum-machine" className="inner-container">
                <PadBank
                    currentPadBank={this.state.currentPadBank}
                />
                <div id="commands">
                    <div id="power">
                        <img src={on} alt="img-power-on"></img>
                    </div>
                    <div id="bank">Bank</div>
                    <div id="display">Display</div>
                    <div id="volume">Volume</div>
                </div>
            </div>
        )
    }
};

export default App;
