import React from 'react';
import DrumPad from './Components/DrumPad';
import audioBank from './Components/audioBank';
import {Howl} from "howler";

class DrumMachine extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            drumPad: ['Q', 'W', 'E', 'A', 'S', 'D', 'Z', 'X', 'C'],
        };
    };

    drumPadClick = (pad) => {
        const url = [...audioBank].filter((sound) => sound.pad === pad)[0].url
        console.log(url)
    };

    render(){
        return (
        <div id="display">
            {this.state.drumPad.map((pad) => {
                return <DrumPad key={pad} pad={pad} onClick={this.drumPadClick}/>
            })}
        </div>)
    }
};

export default DrumMachine;
