import React from 'react';
import bankOne from './Components/audioBank/bankOne';
import bankTwo from './Components/audioBank/bankTwo';
import PadBank from './Components/PadBank';
import on from './Components/images/on.png';
import off from './Components/images/off.png';

class App extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            power: true,
            display: '',
            currentPadBank: bankOne,
            currentPadBankId: 'Heater Kit',
            sliderVal: 0.3
        };
    };

    displayClipName = (name) => {
        if (this.state.power) {
            this.setState({
                display: name
            });
        };
    };

    selectBank = () => {
        if (this.state.power) {
            this.state.currentPadBankId === 'Heater Kit' ? 
                this.setState({
                    currentPadBank: bankTwo,
                    currentPadBankId: 'Smooth Piano Kit',
                }) :
                this.setState({
                    currentPadBank: bankOne,
                    currentPadBankId: 'Heater Kit',
                });
        };
    };

    adjustVolume = () => {
        console.log("adjust volume")
    };

    powerControl = () => {
        this.setState({
            power: !this.state.power
        });
    };

    clearDisplay = () => {
        this.setState({
            display: ''
        });
    };

    render(){
        return(
            <div id="drum-machine" className="inner-container">

                <div id="power">
                        <p>Power</p>
                        {this.state.power ? 
                        <img onClick={this.powerControl} className="power" src={on} alt="power-on"/> :
                        <img onClick={this.powerControl} className="power" src={off} alt="power-off"/> 
                        }
                </div>

                <PadBank
                    currentPadBank={this.state.currentPadBank}
                    displayClipName={this.displayClipName}
                />

                <div id="bank">{this.state.currentPadBankId}</div>
                <div id="display">{this.state.display}</div>
                <div id="volume">Volume</div>

            </div>
        )
    }
};

export default App;
