import React from 'react';
import DrumPad from './Components/DrumPad';
import bankOne from './Components/audioBank';
import {Howl} from "howler";

class DrumMachine extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            bank: [...bankOne],
        };
    };

    componentDidMount() {
        document.addEventListener('keydown', this.handleKeyPress);
    };

    componentWillUnmount() {
        document.removeEventListener('keydown', this.handleKeyPress);
    };

    handleKeyPress = (e) => {
        if (this.state.drumPad.indexOf(e.key.toUpperCase()) !== -1) {
            const url = [...this.state.bank].filter((sound) => sound.keyTrigger === e.key.toUpperCase())[0].url;
            this.soundPlay(url);
        };
    };

    drumPadClick = (url) => {
        this.soundPlay(url);
    }; 

    soundPlay = (src) => {
        const sound = new Howl ({
            src,
            html5: true
        });
        sound.play()
    }

    render(){
        return (
        <div id="display">
            {this.state.bank.map((drumPad) => {
                return <DrumPad 
                    key={drumPad.id}
                    keyCode={drumPad.keyCode}
                    keyTrigger={drumPad.keyTrigger}
                    id={drumPad.id}
                    url={drumPad.url}
                    onClick={this.drumPadClick}
                    />
            })}
        </div>)
    }
};

export default DrumMachine;
