import React from 'react';
import DrumPad from './Components/DrumPad';
import bankOne from './Components/audioBank';
// import {Howl} from "howler";

class DrumMachine extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            bank: [...bankOne],
        };
    };

    componentDidMount() {
        document.addEventListener('keydown', this.handleKeyPress);
    };

    componentWillUnmount() {
        document.removeEventListener('keydown', this.handleKeyPress);
    };

    handleKeyPress = (e) => {
        const keyTrigger = e.key.toUpperCase();
        if (document.getElementById(keyTrigger)) {
            this.playSound(keyTrigger);
        }
    };

    handleClick = ({id, keyTrigger}) => {
        this.playSound(keyTrigger);
    };

    playSound = (keyTrigger) => {
        const sound = document.getElementById(keyTrigger);
        sound.play();
    };

    render(){
        return (
        <div id="drum-machine">
            <div id="drum-pad">
               {this.state.bank.map((drumPad) => {
                return <DrumPad 
                    key={drumPad.id}
                    keyTrigger={drumPad.keyTrigger}
                    id={drumPad.id}
                    url={drumPad.url}
                    onClick={this.handleClick}
                    />
                })} 
            </div>
            <div id="commands">
                <div id="power"></div>
                <div id="display"></div>
                <div id="volume"></div>
                <div id="bank"></div>
            </div>
        </div>)
    }
};

export default DrumMachine;
