import React from 'react';

class DrumPad extends React.Component {

    render(){
        const {keyTrigger, id, url, onClick} = this.props;

        return (
            <button 
                id={id}
                className="drum-pad" 
                onClick={() => onClick({id, keyTrigger})}
            >
                    {keyTrigger}
                    <audio id={keyTrigger} type="audio/mp3" src={url} className="clip"></audio>
            </button>
        )
    }
};

export default DrumPad;
