import React from 'react';
import DrumPad from './DrumPad';

class PadBank extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            displayPadBank: true
        }
    };

    render() {

        const displayPadBank = this.state.displayPadBank;
        const currentPadBank = this.props.currentPadBank;

        return (
        <div id="drum-pad-div">
            {currentPadBank.map((drumPad) => {
                return (
                <DrumPad 
                    key={drumPad.id}
                    keyCode={drumPad.keyCode}
                    keyTrigger={drumPad.keyTrigger}
                    id={drumPad.id}
                    url={drumPad.url}
                    displayClipName={this.props.displayClipName}
                />
                )
                })}
        </div>
        )
    };
};

export default PadBank;
