import React from 'react';

class DrumPad extends React.Component {

    render(){
        const {keyCode, keyTrigger, id, url, onClick} = this.props;

        return (
            <button 
                id={keyCode}
                className="drum-pad" 
                onClick={() => onClick(url)}
            >
                {keyTrigger}
                <audio type="audio/mp3" src={url} className="clip" id={id}></audio>
            </button>
        )
    }
};

export default DrumPad;
