import React from 'react';

class DrumPad extends React.Component {

    render(){
        const {keyCode, keyTrigger, id, url, onClick} = this.props;

        return (
            <button 
                id={keyCode}
                className="drum-pad" 
                onClick={() => {
                    const sound = document.getElementById(id);
                    sound.play();
                }}
            >
                {keyTrigger}
                <audio id={id} type="audio/mp3" src={url} className="clip" ></audio>
            </button>
        )
    }
};

export default DrumPad;
