import React from 'react';

const DrumPad = ({keyCode, keyTrigger, id, url, onClick}) => {
    return (
        <button 
            id={keyCode}
            className="drum-pad" 
            onClick={() => onClick(url)}
        >
            {keyTrigger}
            <audio src={url} className="clip" id={id}></audio>
        </button>
    )
}

export default DrumPad;
